var file_frame;var $=jQuery.noConflict();$(document).on("click",".edit_image",function(e){buttonId=$(e.currentTarget).attr("rel");$("#"+buttonId).trigger("click")});$(document).on("click",".delete_image",function(e){$(e.currentTarget).parent().parent().css({display:"none",background:"transparent"});$(e.currentTarget).parent().parent().prev().val("");$(e.currentTarget).parent().parent().next().css({display:"inline-block"})});$(document).on("click",".upload-image-button",function(e){e.preventDefault();file_frame=wp.media.frames.file_frame=wp.media({title:"choose image",button:{text:"insert image"},multiple:false,editing:true,input_container:$(e.currentTarget).attr("rel"),image_container:$(e.currentTarget).prev(),currentTarget:$(e.currentTarget)});file_frame.on("select",function(){attachment=file_frame.state().get("selection").first().toJSON();return_value=JSON.stringify(attachment);file_frame.options.image_container.css({display:"block",background:"url("+attachment.sizes.thumbnail.url+") no-repeat"});file_frame.options.currentTarget.css({display:"none"});$("#"+file_frame.options.input_container).val(return_value)});file_frame.open()})